import React, { useState, useEffect } from 'react';
import axios from 'axios';

function WebSocketClient() {
  const [socket, setSocket] = useState(null);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [activeUsers, setActiveUsers] = useState([]);
  const [receiver, setReceiver] = useState(null);
  const [senderIp, setSenderIp] = useState("");
  const [senderPort, setSenderPort] = useState("");

  useEffect(() => {
    let ws;

    const initializeWebSocket = () => {
      ws = new WebSocket("ws://192.168.0.132:8880/chat");

      ws.onopen = () => {
        console.log("Connected to WebSocket server");
      };

      ws.onmessage = (event) => {
        console.log("Incoming message:", event.data);
        try {
          const data = JSON.parse(event.data);
          setMessages(prevMessages => [...prevMessages, `${data.sender}: ${data.text}`]);
        } catch (error) {
          console.error("Error parsing incoming message:", error);
        }
      };

      ws.onclose = () => {
        console.log("WebSocket connection closed");
      };

      ws.onerror = (error) => {
        console.error("WebSocket error:", error);
      };

      setSocket(ws);
    };

    initializeWebSocket();

    return () => {
      if (ws) {
        ws.close();
        console.log("WebSocket connection cleaned up");
      }
    };
  }, []);

  useEffect(() => {
    axios.get("http://192.168.0.132:8880/active-users")
      .then(response => {
        const users = Object.keys(response.data);
        setActiveUsers(users);

        // Extract sender's IP and port from the response
        const currentUser = users.find(user => user.startsWith("192.168.0.132"));
        if (currentUser) {
          const [ip, port] = currentUser.split(":");
          setSenderIp(ip);
          setSenderPort(port);
        } else {
          console.error("Current user not found in active users list.");
        }
      })
      .catch(error => {
        console.error("Error fetching active users:", error);
      });
  }, []);

  const sendMessage = () => {
    if (socket && socket.readyState === WebSocket.OPEN && message && receiver) {
      const msg = {
        sender: `${senderIp}:${senderPort}`,
        receiver: receiver,
        text: message,
      };
      console.log("Sending message:", msg);
      socket.send(JSON.stringify(msg));
      setMessages(prevMessages => [...prevMessages, `You: ${message}`]);
      setMessage("");
    } else {
      console.log("Missing data to send message or WebSocket not open");
    }
  };

  return (
    <div>
      <h1>Chat Application</h1>

      <div>
        <h2>Active Users</h2>
        <ul>
          {activeUsers.length > 0 ? (
            activeUsers.map(user => (
              <li key={user} onClick={() => setReceiver(user)}>
                {user}
              </li>
            ))
          ) : (
            <p>No active users found.</p>
          )}
        </ul>
      </div>

      {receiver && (
        <div>
          <h2>Chat with {receiver}</h2>
          <div>
            {messages.length > 0 ? (
              messages.map((msg, index) => (
                <p key={index}>{msg}</p>
              ))
            ) : (
              <p>No messages yet.</p>
            )}
          </div>
          <input
            type="text"
            value={message}
            onChange={e => setMessage(e.target.value)}
            placeholder="Type a message"
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      )}
    </div>
  );
}

export default WebSocketClient;
