import React, { useState, useEffect, useRef } from "react";
import "./css/App.css"; // Create a CSS file for styling
import axios from "axios";

const App = () => {
  const [activeUsers, setActiveUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [senderIp, setSenderIp] = useState(null); // Store sender IP here
  const socketRef = useRef(null);

  useEffect(() => {
    // Establish WebSocket connection
    socketRef.current = new WebSocket("ws://localhost:8880/chat");

    socketRef.current.onopen = () => {
      console.log("WebSocket connection established");

      // Fetch sender's IP after WebSocket is connected
      axios
        .get("http://localhost:8880/getsenderip") // Assuming this API returns the sender's IP
        .then((response) => setSenderIp(response.data))
        .catch((error) => console.error("Error fetching sender IP:", error));

      // Fetch active users after WebSocket is connected
      axios
        .get("http://localhost:8880/getuser")
        .then((response) => setActiveUsers(response.data))
        .catch((error) => console.error("Error fetching active users:", error));
    };

    socketRef.current.onmessage = (event) => {
      const incomingMessage = JSON.parse(event.data);
      setMessages((prevMessages) => [...prevMessages, incomingMessage]);
    };

    socketRef.current.onclose = () => console.log("WebSocket connection closed");

    return () => {
      socketRef.current.close();
    };
  }, []);

  const handleUserSelect = (user) => {
    setSelectedUser(user);
    setMessages([]); // Clear chat history when a new user is selected
  };

  const handleSendMessage = () => {
    if (newMessage.trim() && selectedUser && senderIp) {
      const message = {
        sender: senderIp,
        receiver: selectedUser,
        text: newMessage,
      };
      socketRef.current.send(JSON.stringify(message));
      setMessages((prevMessages) => [...prevMessages, message]);
      setNewMessage("");
    }
  };

  return (
    <div className="chat-container">
      <div className="user-list">
        <h3>Active Users</h3>
        {activeUsers.length > 0 ? (
          activeUsers.map((user, index) => (
            <div
              key={index}
              className={`user-item ${user === selectedUser ? "selected" : ""}`}
              onClick={() => handleUserSelect(user)}
            >
              {user}
            </div>
          ))
        ) : (
          <div>No active users found</div>
        )}
      </div>
      <div className="chat-box">
        {selectedUser ? (
          <>
            <div className="chat-header">Chat with {selectedUser}</div>
            <div className="chat-messages">
              {messages.map((msg, index) => (
                <div
                  key={index}
                  className={`message ${
                    msg.sender === senderIp ? "sent" : "received"
                  }`}
                >
                  <p>{msg.text}</p>
                </div>
              ))}
            </div>
            <div className="chat-input">
              <input
                type="text"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button onClick={handleSendMessage}>Send</button>
            </div>
          </>
        ) : (
          <div className="no-user-selected">Select a user to start chatting</div>
        )}
      </div>
    </div>
  );
};

export default App;
