// src/components/ChatWindow.js
import React from "react";
import { FaPhoneAlt, FaVideo } from "react-icons/fa";
import "../css/ChatWindow.css";

const ChatWindow = ({ selectedUser, messages, onSendMessage }) => {
  const [text, setText] = React.useState("");

  const handleSendMessage = () => {
    if (text.trim()) {
      onSendMessage(text);
      setText("");
    }
  };

  if (!selectedUser) {
    return <div className="chat-window empty">Select a user to start chatting</div>;
  }

  return (
    <div className="chat-window">
      <div className="chat-header">
        <div className="user-info">
          <div className="avatar">
            {selectedUser.profilePicture ? (
              <img src={selectedUser.profilePicture} alt={selectedUser.name} />
            ) : (
              selectedUser.name[0]
            )}
          </div>
          <span className="username">{selectedUser.name}</span>
        </div>
        <div className="chat-actions">
          <FaPhoneAlt className="icon audio-icon" title="Start audio call" />
          <FaVideo className="icon video-icon" title="Start video call" />
        </div>
      </div>
      <div className="chat-body">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.sender === "me" ? "sent" : "received"}`}
          >
            {msg.text}
          </div>
        ))}
      </div>
      <div className="chat-footer">
        <input
          type="text"
          placeholder="Type a message..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatWindow;
