import React, { useState, useEffect, useRef } from 'react';
import './css/App.css'; // Import CSS for styling

function App() {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [ws, setWs] = useState(null);
  const messageBoxRef = useRef(null);
  const [selectedReceiver, setSelectedReceiver] = useState(null);

  const activeUsers = [
    { id: '192.168.0.132:36310', name: 'User 1' },
    { id: '192.168.0.106:56720', name: 'User 2' },
  ];

  useEffect(() => {
    const socket = new WebSocket('ws://192.168.0.132:8880/chat');
  setWs(socket);

  socket.onopen = () => {
    console.log('Connected to WebSocket');
  };

    // Handle incoming messages from the server
    socket.onmessage = (event) => {
      const incomingMessage = JSON.parse(event.data);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: incomingMessage.sender, text: incomingMessage.text },
      ]);
    };

    // Handle errors
    socket.onerror = (error) => {
      console.error('WebSocket error: ', error);
    };

    // Handle WebSocket closure
    socket.onclose = () => {
      console.log('WebSocket connection closed');
    };

    // Set WebSocket connection state
    setWs(socket);

    // Cleanup WebSocket connection when component is unmounted
    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, []);

  const sendMessage = () => {
    if (ws && message.trim() && selectedReceiver) {
      const sender = ''; // Hardcoded or dynamically set sender IP and port
      const receiver = selectedReceiver; // Use the selected receiver from the UI

      const messageData = {
        sender: sender,
        receiver: receiver,
        text: message,
      };

      ws.send(JSON.stringify(messageData));

      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'You', text: message },
      ]);

      setMessage('');
      messageBoxRef.current.focus();
    } else {
      alert('Please select a receiver before sending a message.');
    }
  };

  const handleReceiverSelect = (receiverId) => {
    setSelectedReceiver(receiverId); // Set the selected receiver's IP:port
  };

    return (
    <div className="App">
      <h1>WebSocket Chat</h1>

      <div className="chat-container">
        {/* Active Users List */}
        <div className="users-list">
          <h3>Active Users</h3>
          <ul>
            {activeUsers.map((user) => (
              <li
                key={user.id}
                onClick={() => handleReceiverSelect(user.id)}
                style={{
                  cursor: 'pointer',
                  backgroundColor: selectedReceiver === user.id ? '#ddd' : 'transparent',
                  padding: '5px',
                }}
              >
                {user.name} ({user.id})
              </li>
            ))}
          </ul>
        </div>

        {/* Message Box */}
        <div className="message-box">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              <strong>{msg.sender}:</strong> {msg.text}
            </div>
          ))}
        </div>

        {/* Message Input */}
        <div className="input-area">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            ref={messageBoxRef}
            placeholder="Type your message"
            className="message-input"
          />
          <button onClick={sendMessage} className="send-button">Send</button>
        </div>
      </div>
    </div>
  );
}

export default App;
